INSERT INTO `movie` (`movie_id`, `name_vi`, `name_en`, `poster`, `description`, `release_date`, `duration`, `rated_id`, `trailer`, `type_id`, `url`, `view`, `imdb`, `name_search`) VALUES
(118, 'Joker', 'Joker', 'https://cdn.moveek.com/media/cache/short/5d8851021945a604146068.jpg', 'Joker (2019) lấy bối cảnh những năm 1980, lúc này, kẻ thù của Batman vẫn còn là một diễn viên hài thảm hại mang tên Arthur Fleck. Sau nhiều biến cố lớn, Arthur dần lún sâu hơn vào những trò bệnh hoạn của mình và trở thành một kẻ cuồng sát.', '2019-10-04', 119, 4, 'https://www.youtube.com/embed/lOvsyT-yOXk?hd=1', 2, 'https://moveek.com/phim/joker-movie/', 6, 8.7, 'Joker'),
(119, 'Người Tuyết Bé Nhỏ', 'Abominable', 'https://cdn.moveek.com/media/cache/short/5d6a8aff55713018859339.jpg', 'Trải qua một hành trình dài 3000 dặm từ Thượng Hải đến vùng tuyết của dãy núi Himalaya, một nhóm thám hiểm dũng cảm. đã gặp một người tuyết Yeti trẻ. Và cũng trên ngọn núi đó, cuộc diện kiến một sinh vật ma thuật hứa hẹn sẽ đem đến những trải nghiệm mà họ chưa từng ngờ tới.', '2019-10-04', 97, 1, 'https://www.youtube.com/embed/W2QswFieu5A?hd=1', 2, 'https://moveek.com/phim/abominable/', 1, 7, 'Nguoi Tuyet Be Nho'),
(120, 'Hung Thần Đại Dương: Thảm Sát', '47 Meters Down: Uncaged', 'https://cdn.moveek.com/media/cache/short/5d7b02c204da7189075593.jpg', 'Trong chuyến phiêu lưu đến một vùng đảo ít người biết đến, 4 cô gái Mia, Sasha, Nicole và Alexa quyết định lặn xuống biển để khám phá một thành cổ của người Maya dưới đáy đại dương. Khi vào đến bên trong, cổng thành bỗng sụp đổ khiến họ phải nhanh chóng tìm cách thoát thân trong những đường hầm mê cung và các hang động kỳ quái trước khi cạn sạch dưỡng khí.', '2019-09-27', 89, 3, 'https://www.youtube.com/embed/KE-dXr5C70Q?hd=1', 2, 'https://moveek.com/phim/47-meters-down-uncaged/', 0, 5, 'Hung Than dai Duong: Tham Sat'),
(121, 'Nhân Duyên: Người Yêu Tiền Kiếp', '', 'https://cdn.moveek.com/media/cache/short/5d7a347bec84f413363543.jpg', 'Nhân Duyên: Người Yêu Tiền Kiếp xoay quanh mối tình kéo dài nhiều kiếp giữa chàng trai si tình Trần Nhật và Mai - một cô gái xinh đẹp, dịu dàng. Chỉ vì không muốn quên đi tình yêu dang dở với Mai từ kiếp trước, Trần Nhật quyết tâm chống lại quy luật luân hồi chuyển kiếp, tình nguyện ở cạnh Mai như một chiếc bóng vô hình để bảo vệ cô trước sóng gió cuộc đời.', '2019-10-04', 89, 3, 'https://www.youtube.com/embed/8Nml0UAYV3M?hd=1', 2, 'https://moveek.com/phim/nhan-duyen-nguoi-yeu-tien-kiep/', 1, 0, 'Nhan Duyen: Nguoi Yeu Tien Kiep'),
(122, 'Ngọt Ngào Điên Dại', 'Crazy Romance', 'https://cdn.moveek.com/media/cache/short/5d8ad60202a39094528222.jpg', 'Ngọt Ngào Điên Dại là bộ phim nói về mối tình lãng mạn và đặc biệt của Jae-hoon và Sun-Young. Cả hai đều vừa trải qua những tổn thương trong tình yêu. Khi Jae-hoon bị vị hôn thê bỏ rơi thì Sun-young lại bị chính người yêu mình phản bội.', '2019-10-04', 110, 4, 'https://www.youtube.com/embed/9aORxhDoM1E?hd=1', 2, 'https://moveek.com/phim/crazy-romance/', 1, 6.7, 'Ngot Ngao dien Dai'),
(123, 'Chạy Sô Ăn Cưới', 'The Wedding Year', 'https://cdn.moveek.com/media/cache/short/5d85aa7416cad789465695.jpeg', 'Chạy Sô Ăn Cưới khai thác chủ đề hài hước “dành cả thanh xuân để đi ăn cưới” cùng với câu chuyện tình yêu lãng mạn của cặp đôi Mara và Jake.', '2019-10-04', 91, 4, 'https://www.youtube.com/embed/OxSUbiOdMIU?hd=1', 2, 'https://moveek.com/phim/the-wedding-year/', 1, 5.3, 'Chay So an Cuoi'),
(124, 'Bước Nhảy Cuồng Nhiệt', 'High Strung Free Dance', 'https://cdn.moveek.com/media/cache/short/5d8b14053c730332946702.jpg', 'Barlow - một nữ vũ công luôn chật vật tìm kiếm cơ hội cho mình và Charlie - một tay chơi piano ở các quán ăn. Họ may mắn được lọt mắt xanh của nhà biên đạo trẻ tuổi tài năng Zander cho tour biểu diễn vòng quanh thế giới Free Dance.', '2019-10-04', 103, 2, 'https://www.youtube.com/embed/GNwlrgXsF1U?hd=1', 2, 'https://moveek.com/phim/high-strung-free-dance/', 0, 5.9, 'Buoc Nhay Cuong Nhiet'),
(125, 'Shaun the Sheep: Người Bạn Ngoài Hành Tinh', 'A Shaun the Sheep Movie: Farmageddon', 'https://cdn.moveek.com/media/cache/short/5d7b04926dd8e379033779.jpeg', 'Lu-la, một cô bé ngoài hành tinh hết sức đáng yêu rơi từ chiếc phi thuyền không gian xuống trang trại Mossy Bottom. Thế là chú cừu thông minh Shaun cùng những người bạn bắt tay vào công cuộc giúp đỡ để đưa Lu-la về với quê hương mình.', '2019-09-27', 86, 1, 'https://www.youtube.com/embed/oBDgV36RvIc?hd=1', 2, 'https://moveek.com/phim/a-shaun-the-sheep-movie-farmageddon/', 1, 7.1, 'Shaun the Sheep: Nguoi Ban Ngoai Hanh Tinh'),
(126, 'Trời Sáng Rồi, Ta Ngủ Đi Thôi', 'Good Morning and Good Night', 'https://cdn.moveek.com/media/cache/short/5d5a0d0739f9c515970048.jpg', 'Trời Sáng Rồi, Ta Ngủ Đi Thôi mang một vẻ đẹp quen thuộc: vẻ đẹp của sự mộc mạc, gần gũi và chân thật. Sài Gòn trong những thước phim là khu chung cư cũ, con hẻm nhỏ, quán cơm tấm đêm, đường hầm Thủ Thiêm, con kênh bắc ngang thành phố, ánh đèn đường ngả vàng,… đều là những hình ảnh mà người Sài Gòn trải qua mỗi ngày.', '2019-09-27', 93, 2, 'https://www.youtube.com/embed/RX_IQj5oT7g?hd=1', 2, 'https://moveek.com/phim/troi-sang-roi-ta-ngu-di-thoi/', 0, 6.7, 'Troi Sang Roi, Ta Ngu di Thoi'),
(127, 'Na Tra: Ma Đồng Giáng Thế', 'Ne Zha', 'https://cdn.moveek.com/media/cache/short/5d6a8c9fb8a70956846480.jpg', 'Từ thuở hồng hoang, tinh hoa đất trời tích tụ thành một viên nguyên châu chứa đựng năng lượng khổng lồ. Viên nguyên châu này được Nguyên Thủy Thiên Tôn tách đôi thành linh châu và ma hoàn. Ai sở hữu ma hoàn sẽ biến thành Ma Vương, còn người sở hữu linh châu sẽ trở thành anh hùng cứu thế.', '2019-09-20', 110, 1, 'https://www.youtube.com/embed/LTCcjPZGUnA?hd=1', 2, 'https://moveek.com/phim/ne-zha/', 1, 7.7, 'Na Tra: Ma dòng Giáng Thế'),
(128, 'Khung Hình Chết Chóc', 'Warning Do Not Play', 'https://cdn.moveek.com/media/cache/short/5d89a4dcec6e2216016986.jpg', 'Mi-Jung trong quá trình tìm kiếm đã phớt lờ mọi cảnh bảo từ các đàn em trong ngành và cô đã tìm được những tư liệu còn sót lại của bộ phim bị nguyền rủa đó. Bộ phim đã khiến cô bị cuốn vào nó, cô không còn phân biệt được mình đang ở thực tại hay đang chính trong bô phim đó.', '2019-09-27', 85, 4, 'https://www.youtube.com/embed/JhHLxHrhios?hd=1', 2, 'https://moveek.com/phim/warning-do-not-play/', 0, 5.2, 'Khung Hinh Chet Choc'),
(129, 'Xin Chào, Yêu, Tạm Biệt', 'Hello, Love, Goodbye', 'https://cdn.moveek.com/media/cache/short/5d81b7f859717254628917.jpg', 'Joy là một cô gái trẻ đang làm giúp việc tại Hong Kong phồn hoa. Nhưng giấc mơ của cô là đến Canada để lập nghiệp và sẽ sống cuộc đời mình ở Canada. Tình cờ cô gặp anh chàng batender đào hoa và sát gái Ethan. Chỉ vài năm nữa, Ethan sẽ trở thành công dân Hong Kong và anh sẽ gắn bó cuộc sống ở đây.', '2019-09-27', 117, 2, 'https://www.youtube.com/embed/kgBqqfmfq0I?hd=1', 2, 'https://moveek.com/phim/hello-love-goodbye/', 0, 7.6, 'Xin Chao, Yeu, Tam Biet'),
(130, 'Siêu Quậy Có Bầu', 'Naughty Teenager', 'https://cdn.moveek.com/media/cache/short/5d75ac8b82ec8866781056.jpg', 'Siêu Quậy Có Bầu là câu chuyện về Hạ An – cô nữ sinh có thành tích học tập đáng nể nhưng cũng được mệnh danh là “siêu quậy học đường”. Có một ngày, Hạ An sa vào lưới tình và phát hiện mình dính bầu. Hạ An bị nhà trường phát hiện và đuổi học, gia đình từ chối, bạn bè “miệt thị”, tất cả dồn ép cô vào ngõ cụt để rồi nảy ra ý định tự kết liễu đời mình.', '2019-09-20', 90, 3, 'https://www.youtube.com/embed/QHLR6Dfciz8?hd=1', 2, 'https://moveek.com/phim/sieu-quay-co-bau/', 1, 0, 'Sieu Quay Co Bau'),
(131, 'Sát Thủ Anna', 'Anna', 'https://cdn.moveek.com/media/cache/short/5d6de78e0532c859386116.jpg', 'Ẩn sau vẻ đẹp thần bí của Anna là những kỹ năng chiến đấu vô cùng ấn tượng. Điều này biến cô trở thành một trong những sát thủ nguy hiểm nhất thế giới.', '2019-09-20', 119, 4, 'https://www.youtube.com/embed/zwOT_M_hCUc?hd=1', 2, 'https://moveek.com/phim/anna/', 1, 5.5, 'Sat Thu Anna'),
(132, 'Giải Mã Bí Ẩn Ngân Hà', 'Ad Astra', 'https://cdn.moveek.com/media/cache/short/5d80183b49600363593004.jpg', 'Giải Mã Bí Ẩn Ngân Hà là câu chuyện viễn tưởng về phi hành gia Roy McBride và hành trình đến rìa Hệ Mặt Trời để tìm người cha mất tích. Tại đây, Roy khám phá mối hiểm họa đe dọa sự tồn vong của nhân loại.', '2019-09-20', 122, 2, 'https://www.youtube.com/embed/RWB6R_w7zdw?hd=1', 2, 'https://moveek.com/phim/ad-astra/', 0, 6.9, 'Giai Ma Bi an Ngan Ha'),
(133, 'Tru Tiên', 'Jade Dynasty', 'https://cdn.moveek.com/media/cache/short/5d75c9774538e537413952.jpg', 'Trương Tiểu Phàm sau khi trảm qua thảm án ở thôn Thảo Miếu đã gia nhập vào Thanh Vân môn. Trong một lần vô tình, Tiểu Phạm có được gậy &quot;nhiếp hồn&quot;.', '2019-09-20', 100, 3, 'https://www.youtube.com/embed/26GRpMBgCiA?hd=1', 2, 'https://moveek.com/phim/jade-dynasty/', 0, 5.1, 'Tru Tien'),
(134, 'Long Ấn Cơ Mật', 'Journey to China: The Iron Mask Mystery', 'https://cdn.moveek.com/media/cache/short/5d78f0c227a94038284905.jpg', 'Nhà thám hiểm Jonathan Green nhận lệnh từ Peter Đại Đế phải hoàn thành bản đồ miền Đông nước Nga. Trong cuộc hành trình ẩn chứa nhiều điều bất ngờ, anh đã đặt chân đến Trung Quốc và đối mặt với nhiều sinh vật kỳ quái.', '2019-09-20', 120, 1, 'https://www.youtube.com/embed/EpzBPjJDoFQ?hd=1', 2, 'https://moveek.com/phim/journey-to-china-the-iron-mask-mystery/', 2, 4.7, 'Long an Co Mat'),
(135, 'Ngày Hôm Qua', 'Yesterday', 'https://cdn.moveek.com/media/cache/short/5d5e0d6923f22395599764.jpg', 'Sẽ ra sao nếu như một ngày cả thế giới bỗng nhiên quên mất một huyền thoại âm nhạc mang tên The Beatles? Và chỉ duy nhất một chàng ca sĩ vô danh Jack Malik nhớ đến cái tên bất hủ đó.', '2019-09-20', 116, 3, 'https://www.youtube.com/embed/tCIwqvh8Je8?hd=1', 2, 'https://moveek.com/phim/yesterday/', 0, 6.9, 'Ngay Hom Qua'),
(136, 'Tiên Hắc Ám 2', 'Maleficent: Mistress of Evil', 'https://cdn.moveek.com/media/cache/short/5d959b074deaf440657669.jpg', 'Mặc dù mối quan hệ của Maleficent và Aurora được tạo dựng từ những tổn thương, thù hận rồi sau đó mới đến tình yêu thương nhưng cuối cùng thì nó cũng đã đơm hoa kết trái. Tuy vậy, xung đột giữa hai giới: loài người và tiên tộc vẫn vẫn luôn hiện hữu.', '2019-10-18', 120, 2, 'https://www.youtube.com/embed/kMjFcg9AXwQ?hd=1', 2, 'https://moveek.com/phim/maleficent-mistress-of-evil/', 17, 7, 'Tien Hac am 2'),
(137, 'Bắc Kim Thang', 'Home Sweet Home', 'https://cdn.moveek.com/media/cache/short/5d64022933476925354600.jpg', 'Chàng trai trở về nhà sau 6 tháng ở bệnh viện. Gia đình cậu cho hay, em họ cậu đã tự sát, nhưng cô gái cứ thường xuyên xuất hiện quanh cậu.', '2019-10-25', 86, 4, 'https://www.youtube.com/embed/2rVcJwWh6Tw?hd=1', 2, 'https://moveek.com/phim/bac-kim-thang/', 2, 6.3, 'Bac Kim Thang'),
(138, 'Thất Sơn Tâm Linh', 'Kumanthong', 'https://cdn.moveek.com/media/cache/short/5d80e64850ccd262269181.jpg', 'Thất Sơn Tâm Linh là câu chuyện kể về gã thầy thuốc sát nhân tên Huỳnh. Với tướng mạo hiền từ, cách nói chuyện nhẹ nhàng, từ tốn, thường xuyên chữa bệnh mà không lấy tiền, hắn tạo được lòng tin của rất nhiều người dân ở nơi hắn đến. Hắn ta luôn gieo rắc vào đầu người khác về những bài thuốc bí truyền, những cách chữa bệnh kỳ lạ mang đậm màu sắc kì dị…Nhưng đằng sau đó là  một âm mưu khủng khiếp!', '2019-10-09', 102, 4, 'https://www.youtube.com/embed/snbNTFWvIhs?hd=1', 1, 'https://moveek.com/phim/that-son-tam-linh/', 0, 6.2, 'That Son Tam Linh'),
(139, 'Đàn Ông Song Tử', 'Gemini Man', 'https://cdn.moveek.com/media/cache/short/5d7af00722506421639161.jpg', 'Câu chuyện về tên sát thủ già Henry Brogen đang sắp sửa “xuống tay gác kiếm” thì đột nhiên bị truy sát bởi một người lạ bí ẩn. Đặc biệt hơn khi kẻ đang săn lùng anh dường như có khả năng phán đoán ra mọi đường đi nước bước của Henry. Trải qua quá trình điều tra, Henry Brogan phát hiện ra rằng sát thủ được cử tới chính là phiên bản nhân bản vô tính của anh cách đây 25 năm về trước.', '2019-10-11', 117, 1, 'https://www.youtube.com/embed/gdGgcu25j5Y?hd=1', 2, 'https://moveek.com/phim/gemini-man/', 1, 5.7, 'dan ong Song Tu'),
(140, 'Dì Ơi, Đừng Có Bồ', 'Love and Run', 'https://cdn.moveek.com/media/cache/short/5d8f75c2e2f3c476391713.jpg', 'Due một cậu thiếu niên cứng đầu, không thích bán mì nối nghiệp gia đình nên bỏ lên Bangkok kiếm sống. Cuộc sống cơm ba bữa êm đềm của Due bỗng bị nhân tố thuộc hàm biến thiên mang tên “bà dì” xen vào.', '2019-10-18', 101, 3, 'https://www.youtube.com/embed/7gEQWqRBp3s?hd=1', 2, 'https://moveek.com/phim/love-and-run/', 0, 6.2, 'Di oi, dung Co Bo'),
(141, 'Điều Ước Cuối Của Mẹ', 'The Big Shot', 'https://cdn.moveek.com/media/cache/short/5d91ce955ba6b823274219.jpg', 'Ở một làng chài nghèo khó vô danh ở tỉnh Jeolla, người phụ nữ Soon-ok sống cùng con trai Ki-kang và con gái Ki-soon. Ki-kang là một nam thanh niên sáng sủa, được người dân trên đảo yêu quý và đặt nhiều kỳ vọng về một tiền đồ rộng mở. Ki-Kang vì muốn thành công nên đã quyết định đến Seoul để thực hiện ước mơ. Thế nhưng, anh lại trở thành một tội phạm lĩnh án tử hình.', '2019-10-25', 108, 4, 'https://www.youtube.com/embed/WzCq56KOXnU?hd=1', 2, 'https://moveek.com/phim/a-diamond-in-the-rough/', 1, 5.7, 'dieu uoc Cuoi Cua Me'),
(142, 'Biệt Đội Bất Hảo', 'The Bad Guys: Reign of Chaos', 'https://cdn.moveek.com/media/cache/short/5d804b0d16338076728922.jpg', 'Đội Điều Tra Đặc Biệt do đội trưởng Oh Gu-tak thành lập, với tiêu chí không cần quan tâm phương pháp tốt xấu, chỉ cần “bắt sống bọn tội phạm”. Thành viên gạo cội nhất của đội là tay đấm Park Woong-chul, người đã vào tù cũng là đại ca được tôn kính. Hai thành viên mới được chiêu mộ thêm là mỹ nữ chân dài bốc lửa - thiên tài lừa đảo Kwak No-soon và tay cảnh sát lì lợm đang lĩnh án Ko Yoo-sung.', '2019-10-11', 115, 4, 'https://www.youtube.com/embed/z6ukWvQ-YH8?hd=1', 2, 'https://moveek.com/phim/the-bad-guys-reign-of-chaos/', 0, 0, 'Biet doi Bat Hao'),
(143, 'Lời Nguyền Trên Biển', 'Mary', 'https://cdn.moveek.com/media/cache/short/5d9aa16723c7a284136935.jpg', 'Trong chuyến lênh đênh trên đại dương, cả nhà David sẽ đối mặt với bí mật kinh hoàng ẩn dật bên trong con tàu.', '2019-10-18', 82, 4, 'https://www.youtube.com/embed/PiP_b4Y47rQ?hd=1', 2, 'https://moveek.com/phim/mary/', 0, 0, 'Loi Nguyen Tren Bien'),
(144, 'Chuyện Ngày Mưa Ở New York', 'A Rainy Day in New York', 'https://cdn.moveek.com/media/cache/short/5d971165db2ae429287976.jpg', 'Chàng sinh viên Gatsby Welles theo chân bạn gái Ashleigh Enright tới Manhattan, New York vào dịp cuối tuần vì Ashleigh giành được cơ hội phỏng vấn đạo diễn Roland Pollard. Thay vì một chuyến đi đặc biệt và lãng mạn như kỳ vọng, Gatsby liên tục bị bạn gái bỏ rơi. Ashleigh thơ ngây đeo đuổi cuộc phiêu lưu chớp nhoáng bên 3 người đàn ông “sừng sỏ” của Hollywood. Gatsby lang thang trên đường phố New York, tình cờ gặp gỡ nữ diễn viên Shannon và đóng chung với nàng một cảnh hôn đầy lãng mạn và khám phá ra nhiều điểm chung giữa hai người.', '2019-10-18', 92, 3, 'https://www.youtube.com/embed/HqsqVtJr7gs?hd=1', 2, 'https://moveek.com/phim/a-rainy-day-in-new-york/', 0, 6.9, 'Chuyen Ngay Mua o New York'),
(145, 'Nghĩa Địa Ma', 'Ghost in the Graveyard', 'https://cdn.moveek.com/media/cache/short/5da549c9de57e899402649.jpg', 'Thị trấn Mt. Moriah xuất hiện Martha, một hồn ma quay trở lại ám ảnh thanh thiếu niên, những người chứng kiến ​​cái chết của cô khi còn nhỏ, trong một &quot;trò chơi&quot; của ở nghĩa địa ma.', '2019-10-18', 90, 4, 'https://www.youtube.com/embed/92eH-5iGy0c?hd=1', 2, 'https://moveek.com/phim/ghost-in-the-graveyard/', 0, 5.1, 'Nghia dia Ma'),
(146, 'Thú Cưng Siêu Quậy', 'Trouble', 'https://cdn.moveek.com/media/cache/short/5d92de2679260558915696.jpg', 'Từ khi còn là một chú cún con, Rắc Rối đã sống một cuộc sống xa hoa trong một biệt thự lớn. Ở  đó có những bữa ăn trưa với thịt bò thăn và ngủ trên những chiếc khăn lông mềm mại nhất.  Nhưng sau khi ông chủ giàu có qua đời, không còn ai quan tâm đến, Rắc Rối thấy mình thật cô  đơn và lạc lõng, chú quyết định sẽ bước ra khỏi vùng an toàn và khám phá thế giới bên ngoài  căn biệt thự.', '2019-10-11', 87, 1, 'https://www.youtube.com/embed/fkV8iwVyvJ0?hd=1', 1, 'https://moveek.com/phim/trouble/', 1, 7.6, 'Thu Cung Sieu Quay'),
(147, 'Lời Từ Biệt', 'The Farewell', 'https://cdn.moveek.com/media/cache/short/5d805fc9e86b0329413696.jpg', 'Cả gia đình của Bili từ New York quay trở về Trung Quốc để tham dự một đám cưới giả - cái cớ để cho cả đại gia đình được đoàn tụ khi biết tin người bà của mình chỉ còn vài tuần để sống. Trong khi đó, bà lại là người duy nhất hoàn toàn không biết đến căn ác bệnh của mình.', '2019-10-11', 100, 2, 'https://www.youtube.com/embed/R4efqDBrbKo?hd=1', 2, 'https://moveek.com/phim/the-farewell/', 0, 6.3, 'Loi Tu Biet'),
(148, 'Vùng Đất Thây Ma: Cú Bắn Đúp', 'Zombieland: Double Tap', 'https://cdn.moveek.com/media/cache/short/5d889a8369cd4367870997.jpg', 'Cả nhóm phải đối mặt với 1 loại thây ma đã tiến hóa - to xác hơn, khỏe hơn và chạy nhanh hơn và chúng thích đi săn theo bầy.', '2019-10-25', 99, 4, 'https://www.youtube.com/embed/1N0HJ9X47Uk?hd=1', 2, 'https://moveek.com/phim/zombieland-double-tap/', 3, 7.2, 'Vung dat Thay Ma: Cu Ban dup'),
(149, 'Gia Đình Addams', 'The Addams Family', 'https://cdn.moveek.com/media/cache/short/5d6a8ad26888c037459458.jpg', 'Cuộc sống gia đình Addams bị đảo lộn bởi sự xuất hiện trước cửa nhà đầy bất ngờ một người dẫn chương trình truyền hình thực tế xảo trá, trong khi họ đang chuẩn bị đón chào họ hàng thân thích đến dự đại lễ kỷ niệm của cả gia đình.', '2019-10-25', 87, 2, 'https://www.youtube.com/embed/HjGUPbrQ3Z4?hd=1', 2, 'https://moveek.com/phim/the-addams-family/', 1, 5.9, 'Gia dinh Addams'),
(150, 'Ứng Dụng Tử Thần', 'Countdown', 'https://cdn.moveek.com/media/cache/short/5da5730f1f205926449993.jpg', 'Một nữ y tá trẻ đã tải về ứng dụng dự đoán cái chết đang gây xôn xao trong cộng đồng. Tuy nhiên, ứng dụng dự đoán cô chỉ còn vỏn vẹn 3 ngày để sống, trong khi đó cô cũng dần cảm nhận được một thực thể kỳ quái đang săn lùng mình.', '2019-10-25', 90, 4, 'https://www.youtube.com/embed/JJPRkYPrAKo?hd=1', 2, 'https://moveek.com/phim/countdown/', 1, 5.4, 'ung Dung Tu Than'),
(152, 'Tiếng Hát Nhân Ngư', 'Lu over the Wall', 'https://cdn.moveek.com/media/cache/short/5d68e790ef775920772325.jpg', 'Kai, một học sinh cấp II rời Tokyo về sinh sống tại một làng chài thuộc Tỉnh Hinashi sau khi cha mẹ cậu li dị. Cuộc sống chán chường trôi qua, bỗng một ngày cậu được hai người bạn cùng lớp mời tham gia vào ban nhạc của họ. Kai dù miễn cưỡng nhưng vẫn chấp nhận đến tập cùng cả nhóm ở Đảo Nhân ngư, nơi cậu gặp người cá Lu.', '2019-09-15', 107, 1, '', 2, 'https://moveek.com/phim/lu-over-the-wall/', 2, 6.8, 'Tieng Hat Nhan Ngu'),
(153, 'Ngôi Nhà Bươm Bướm', 'Butterfly', 'https://cdn.moveek.com/media/cache/short/5d142eb2e676d964282159.jpg', 'Ngôi Nhà Bươm Bướm nói về hành trình vượt qua trở ngại của Hoàng và Mai để đi đến cuộc hôn nhân mơ ước. Qua nhiều phen “chiêu dụ”, Hoàng và Mai đã sắp xếp được buổi gặp cho hai gia đình. Nhưng chính từ đây, câu chuyện oái oăm của nhà trai bị phơi bày và đem đến tình cảnh có một không hai cho buổi gặp mặt.', '2019-08-30', 100, 4, 'https://www.youtube.com/embed/K0fhkzAzTHI?hd=1', 2, 'https://moveek.com/phim/ngoi-nha-buom-buom/', 0, 6.8, 'Ngoi Nha Buom Buom'),
(162, 'Kẻ Hủy Diệt: Vận Mệnh Đen Tối', 'Terminator: Dark Fate', 'https://cdn.moveek.com/media/cache/short/5d68ac26188d4831414600.jpg', 'Chào mừng đến với thế giới hậu Ngày Phán Xét.', '2019-11-01', 128, 4, 'https://www.youtube.com/embed/7SygWlQ9r-E?hd=1', 2, 'https://moveek.com/phim/terminator-dark-fate/', 1, 6.5, 'Ke Huy Diet: Van Menh den Toi'),
(163, 'Kim Ji-young 1982', 'Kim Ji-young 1982', 'https://cdn.moveek.com/media/cache/short/5da7fe5b9d2a9387077061.jpg', 'Kim Ji-young - một phụ nữ bình thường đang trải qua tháng ngày làm vợ, làm mẹ. Những câu chuyện đời thường tưởng chừng ai cũng biết, cho đến một ngày Ji-young nhận ra những điều kỳ lạ. Dường như cô đang là &quot;nơi tạm trú&quot; cho những người phụ nữ rất gắn bó với cuộc đời mình.', '2019-11-01', 119, 3, 'https://www.youtube.com/embed/4uRN3fXdBE0?hd=1', 2, 'https://moveek.com/phim/kim-ji-young-1982/', 1, 7.7, 'Kim Ji-young 1982'),
(164, 'Đại Dịch Tử Thần', 'Infection', 'https://cdn.moveek.com/media/cache/short/5d9dd4f40504c634088197.jpg', 'Sự bùng phát của virus dại đột biến tạo nên một đại dịch, nhấn chìm thành phố trong hỗn loạn và sợ hãi. Một người cha phải tìm cách chiến đấu qua những khu vực nhiễm bệnh để giải cứu đứa con trai duy nhất của mình.', '2019-10-31', 91, 4, 'https://www.youtube.com/embed/lfQdyz8vPI4?hd=1', 2, 'https://moveek.com/phim/infection/', 0, 6.6, 'dai Dich Tu Than'),
(165, 'Salma và Điều Ước Nhiệm Màu', 'Día de muertos', 'https://cdn.moveek.com/media/cache/short/5dafbee21773c620342157.jpg', 'Tại thị trấn Santa Clara của Mexico, Salma là một đứa trẻ mồ côi 16 tuổi chưa bao giờ được gặp cha mẹ ruột. Câu chuyện duy nhất cô bé được nghe kể là họ đã bỏ rơi cô. Salma dành phần lớn cuộc đời của mình để tìm kiếm manh mối về danh tính của cha mẹ và nơi ở của họ.', '2019-11-01', 90, 2, 'https://www.youtube.com/embed/a3TD36hJmwI?hd=1', 2, 'https://moveek.com/phim/dia-de-muertos/', 4, 4.6, 'Salma va dieu uoc Nhiem Mau'),
(166, 'Đồi Địa Đàng', 'Paradise Hills', 'https://cdn.moveek.com/media/cache/short/5da7fe48f1975571395421.jpg', 'Uma thức giấc trên một hòn đảo gọi là ĐỒI ĐỊA ĐÀNG được tạo ra để quản lý và giáo dục những cô gái không đứng đắn. Dẫn dắt bởi Duchess, hòn đảo tạo ra một cuộc sống trong mơ cho những &quot;bệnh nhân&quot; của nó. Tuy nhiên, phía sau những câu chuyện thần tiên là những bí mật được thêu dệt tinh vi, là khởi nguồn cho cuộc nổi dậy thoát khỏi chốn Thiên Đường.', '2019-11-01', 95, 3, 'https://www.youtube.com/embed/M43_sgkbVHQ?hd=1', 2, 'https://moveek.com/phim/paradise-hills/', 1, 5.9, 'doi dia dang'),
(167, 'Búp Bê Ký Ức Violet Evergarden', 'Violet Evergarden: Eternity and the Auto Memories Doll', 'https://cdn.moveek.com/media/cache/short/5dae9e00d28f4382831895.jpg', 'Búp Bê Ký Ức Violet Evergarden lấy bối cảnh khi nữ chiến binh người máy Violet Evergarden đã giải ngũ và bắt đầu công việc viết thư. Violet tình cờ gặp gỡ Isabella York - một cô bé bị bắt nhập học tại một học viện dành riêng cho con gái của các gia đình danh giá để thực hiện theo giao kèo với bố. Từ đấy, Violet và Issabelle chia sẻ với nhau nhiều điều về cuộc sống ...', '2019-11-01', 91, 2, 'https://www.youtube.com/embed/wSZulNkl9tQ?hd=1', 2, 'https://moveek.com/phim/violet-evergarden/', 2, 8.2, 'Bup Be Ky uc Violet Evergarden'),
(168, 'Thiên Sứ Không Phép Màu', 'Angel Without Miracle', 'https://cdn.moveek.com/media/cache/short/5da403a562918544505062.jpg', 'Trung Dũng, một thanh niên có khả năng nhìn thấy được tương lai những vụ án, tai nạn chết người sắp xảy ra. Nhờ vào khả năng đặc biệt này, anh đã cứu được rất nhiều người. Trớ trêu thay, anh lại không cứu được chính người mẹ ruột thịt và cô người yêu mà anh rất yêu thương.', '2019-11-01', 84, 2, 'https://www.youtube.com/embed/5A8QwOJ1ci4?hd=1', 2, 'https://moveek.com/phim/thien-su-khong-phep-mau/', 1, 0, 'Thien Su Khong Phep Mau'),
(169, 'Vượt Biển', 'Across the Waters', 'https://cdn.moveek.com/media/cache/short/5da72f42259dd212116341.jpg', 'Arne Itkin, một nhạc công guitar chuyên chơi nhạc jazz dường như miễn nhiễm với tất cả các biến động khốc liệt của cuộc chiến tranh do chính phủ Đan Mạch chọn phương thức quan hệ hòa hảo với nước Đức Phát xít. Anh hoài nghi khi Miriam, vợ mình, hoảng sợ vì nghe được tin đồn những người Đan Mạch gốc Do Thái sẽ bị gom lại và bị trục xuất.   Tuy vậy, một cuộc lùng soát vào ban đêm đã buộc họ phải đem cậu con trai 5 tuổi Jakob chạy trốn. Trong tình thế hiểm nguy phải đối phó với lực lượng Gestapo và đồng bọn, gia đình họ phải đặt số phận mình vào tay của những người lạ, những người mà sự chính trực cũng như động cơ không phải lúc nào cũng rõ ràng.', '2019-10-31', 94, 2, 'https://www.youtube.com/embed/i7dygX_AJ3I?hd=1', 2, 'https://moveek.com/phim/fuglene-over-sundet/', 1, 6.2, 'Vuot Bien'),
(170, 'Pháp Sư Mù', 'Blind Shaman', 'https://cdn.moveek.com/media/cache/short/5da6785bc9dd8212247931.jpg', 'Pháp Sư Mù kể lại hành trình đi tìm lại ánh sáng cho đôi mắt của Tinh Lâm và vô số câu chuyện hấp dẫn xảy ra trong quá trình điều trị đôi mắt.', '2019-11-08', 117, 4, 'https://www.youtube.com/embed/IfLG_q0Z--E?hd=1', 2, 'https://moveek.com/phim/phap-su-mu-ai-chet-gio-tay/', 6, 0, 'Phap Su Mu'),
(171, 'Ký Ức Kinh Hoàng', 'Doctor Sleep', 'https://cdn.moveek.com/media/cache/short/5db5960502e40387890435.jpg', 'Ký Ức Kinh Hoàng lấy bối cảnh nhiều năm sau The Shining (1980), khi cậu bé Danny năm nào giờ đây đã trưởng thành nhưng liên tục chìm trong rượu, những cơn thịnh nộ vô chừng và phải đối diện với những hồn ma trong quá khứ. Quyết định làm lại cuộc đời, Danny đã dọn đến sống tại New Hampshire. Trong lúc đó, Danny dần phát hiện ra mình có một năng lực siêu nhiên mạnh mẽ và có thể kết nối với cô bé 12 tuổi tên Abra Stone. Từ đấy, Danny và Abra đoàn kết cùng nhau để chống lại Rose the Hat và những kẻ tay sai - The True Knot - những kẻ chuyên truy sát những người có năng lực đặc biệt như Danny và Abra để có cuộc sống bất tử.', '2019-11-08', 151, 4, 'https://www.youtube.com/embed/g_GH_U-BZyc?hd=1', 2, 'https://moveek.com/phim/doctor-sleep/', 13, 7.6, 'Ky uc Kinh Hoang'),
(172, 'Đùa Với Lửa', 'Playing With Fire', 'https://cdn.moveek.com/media/cache/short/5db666d1a5b36647710480.jpg', 'Jake Carson - đội trưởng đội phòng cháy chữa cháy, cực kì nghiêm túc trong công việc. Khi vụ cháy rừng xảy ra và ngày càng lan rộng, đội cứu hỏa tinh nhuệ lên đường thực hiện nhiệm vụ giải cứu ba anh chị em đang mắc kẹt trong một căn nhà. Không thể xác định vị trí của cha mẹ chúng, cả đội bỗng trở thành những người giữ trẻ “bất đắc dĩ”. Đây có lẽ là thử thách khó khăn nhất mà họ chưa từng được đào tạo qua bất kì lớp huấn luyện nào. Từ đây, ba chàng lính cứu hỏa nhận ra cả cuộc sống và công việc của mình sắp bị đảo lộn. Bởi lẽ họ sẽ học được một điều: những đứa trẻ cũng giống như đám cháy, chúng rất dữ dội và không thể lường trước.', '2019-11-08', 96, 1, 'https://www.youtube.com/embed/PIeAY1sUSK0?hd=1', 2, 'https://moveek.com/phim/playing-with-fire/', 1, 4.6, 'dua Voi Lua'),
(173, 'Ma Gương 2', 'Kuntilanak 2', 'https://cdn.moveek.com/media/cache/short/5da7fe2b9e1bc755089142.jpg', 'Sau khi thoát được khỏi tay Kuntilanak, Dinna đến gặp một người tự xưng là mẹ ruột của cô. Tuy nhiên, Dinna và 2 người em của mình lại bị mắc kẹt trong một ngôi nhà giữa rừng sâu. Những hiện tượng kỳ quái bắt đầu xuất hiện và Dinna nhận ra mình phải đối đầu với Kuntilanak một lần nữa.', '2019-11-08', 100, 4, '', 2, 'https://moveek.com/phim/kuntilanak-2/', 1, 4.6, 'Ma Guong 2'),
(174, 'Khun Phaen: Huyền Thoại Bắt Đầu', 'Khun Phaen Begins', 'https://cdn.moveek.com/media/cache/short/5dafcdb9c65e3077224364.jpg', 'Khun Phaen: Huyền Thoại Bắt Đầu xoay quanh Kaew của Khun Chang Khun Phaen. Không nơi nương tựa lẫn kí ức, chàng trai trẻ trở thành một trên trộm vặt lang thang khắp nơi để “phá làng phá xóm” cùng cậu bạn thân Petch. Khi đến Ayutthaya, anh chàng bất ngờ gặp lại hai người bạn thời thơ ấu là Chang và Pim. Kaew dần nhớ lại những câu chuyện đau lòng năm xưa và phát hiện ra cha mình là vốn là một quý tộc bị xử tử vì tội phản nghịch.', '2019-11-08', 111, 3, 'https://www.youtube.com/embed/8G27cz92Mmw?hd=1', 2, 'https://moveek.com/phim/khun-phaen-begins/', 1, 7.5, 'Khun Phaen: Huyen Thoai Bat dau'),
(175, 'Bố Tớ Là Đô Vật Phản Diện', 'My Dad Is A Heel Wrestler', 'https://cdn.moveek.com/media/cache/short/5dba5a2843cd9987095231.jpg', 'Đô vật Takashi Omura đứng giữa ánh đèn rọi chiếu sáng để đón nhận những tràng vỗ tay nồng nhiệt từ đám đông cổ vũ. Đó là hình ảnh của 10 năm trước, khi anh còn là một ngôi sao trên sàn đấu vật. 10 năm sau đó, với nỗ lực lấy lại phong độ không thành, Takashi đành ngậm ngùi trở thành đô vật phản diện. Cậu con trai Shota 9 tuổi một ngày phát hiện ra sự thật', '2019-11-08', 110, 2, '', 2, 'https://moveek.com/phim/my-dad-is-a-heel-wrestler/', 2, 7.4, 'Bo To La do Vat Phan Dien'),
(176, 'Hồn Papa, Da Con Gái', 'Daddy Issues', 'https://cdn.moveek.com/media/cache/short/5bcd4869b9438771034867.jpg', 'Sau khi hoán đổi cơ thể cho nhau, một người cha mãi không chịu lớn và cô con gái tính tình như “bà cụ non” phải học cách sống đúng với tuổi của mình để trở về cơ thể cũ.', '2018-12-28', 115, 3, 'https://www.youtube.com/embed/TCZGREJYKdQ?hd=1', 2, 'https://moveek.com/phim/hon-papa-da-con-gai/', 2, 6.6, 'Hon Papa, Da Con Gai'),
(177, 'Hoa Hậu Giang Hồ', 'Miss Gangster', 'https://cdn.moveek.com/media/cache/short/5db30c23752e7508399965.jpg', 'Có những câu chuyện từng được kể rất nhiều, có những ‘giai thoại’ mà ai cũng biết về các cuộc thi hoa hậu – nhưng lại chưa từng được đưa lên màn ảnh.', '2019-11-15', 92, 3, 'https://www.youtube.com/embed/oidcK0deos0?hd=1', 2, 'https://moveek.com/phim/hoa-hau-giang-ho/', 26, 0, 'Hoa Hau Giang Ho'),
(178, 'Những Thiên Thần Của Charlie', 'Charlie\'s Angels', 'https://cdn.moveek.com/media/cache/short/5d1a0acb7bab0657767591.JPG', 'Lúc này, Sở Townsend đã trở thành công ty an ninh, điều tra tư nhân hết sức hùng hậu, có quy mô hoạt động trải dài trên toàn thế giới. Vào một ngày, nàng điệp viên Jane bất ngờ nhận được nhiệm vụ giải cứu yếu nhân Elena - nữ kỹ sư phần mềm đang nắm giữ thông tin quan trọng về một vũ khí hủy diệt hàng loạt - khỏi tay bọn sát thủ. Sau khi đưa nàng ta quay về trụ sở an toàn, vì nhận thấy tiềm năng thú vị nơi Elena, đặc vụ cấp cao Bosley liền ngỏ lời mời cô gia nhập tổ chức, đồng thời phối hợp với Jane và Sabina nhằm tìm cách phá hủy thứ vũ khí đáng sợ kia.', '2019-11-15', 118, 3, 'https://www.youtube.com/embed/zDe-CCp8cD8?hd=1', 2, 'https://moveek.com/phim/charlie-s-angels/', 16, 4, 'Nhung Thien Than Cua Charlie'),
(179, 'Bỏ Rơi', 'Stray', 'https://cdn.moveek.com/media/cache/short/5dc3a8ea1ed0a753876862.jpg', 'Sau khi mất đứa con trai duy nhất, Polina và Igor quyết định chữa lành nỗi đau này bằng việc nhận con nuôi. Mọi rắc rối bắt đầu từ khi hai vợ chồng gặp được cậu bé &quot;đặc biệt&quot; và quyết định nhận đứa bé này làm con nuôi.', '2019-11-15', 89, 4, 'https://www.youtube.com/embed/HVtKenO4RjU?hd=1', 2, 'https://moveek.com/phim/stray/', 14, 6.8, 'Bo Roi'),
(190, 'Một Khúc Tình Ca', 'Little Love Song', 'https://cdn.moveek.com/media/cache/short/5dba5e01798f2583762375.jpg', 'Tại một thị trấn nhỏ nằm trên bán đảo Okinawa, ngôi nhà chung của hai quốc gia Nhật Bản và Hoa Kỳ, có một nhóm nhạc trung học chuyên biểu diễn các ca khúc do nhóm tự sáng tác gây được sự chú ý của một hãng thu âm đến từ Tokyo và hãng có ý định hợp tác sản xuất nhạc cùng cả nhóm. Thế nhưng, ngay trước con đường trải đầy ánh hào quang ấy thì...', '2019-11-13', 123, 2, '', 2, 'https://moveek.com/phim/little-love-song/', 13, 6.7, 'Mot Khuc Tinh Ca'),
(198, 'Nữ Hoàng Băng Giá 2', 'Frozen 2', 'https://cdn.moveek.com/media/cache/short/5d959c06d729c742466280.jpg', 'Một chuyến phiêu lưu mới, đầy ma thuật của hai chị em Nữ hoàng Elsa và Công chúa Anna.', '2019-11-22', 103, 1, 'https://www.youtube.com/embed/RE2Vw-RItYY?hd=1', 1, 'https://moveek.com/phim/frozen-2/', 23, 4.3, 'Nu Hoang Bang Gia 2'),
(199, 'Ngốc Ơi Tuổi 17', 'Silly 17', 'https://cdn.moveek.com/media/cache/short/5dc28c4437824029094425.jpg', 'Min Min - cô gái ngổ ngáo nhưng ngây thơ. Một ngày nọ, cô phát hiện mang thai khi đang học lớp 12. Min Min phải thích nghi với cuộc sống mới, đồng thời cố che giấu gia đình, bạn bè.', '2019-11-22', 90, 3, 'https://www.youtube.com/embed/VgigQp9rsLw?hd=1', 2, 'https://moveek.com/phim/ngoc-oi-tuoi-17/', 32, 0, 'Ngoc oi Tuoi 17'),
(207, 'Anh Trai Yêu Quái', 'Dear Devil Brother', 'https://cdn.moveek.com/media/cache/short/5db7d36e18334636162504.jpg', 'Hai anh em cùng cha khác mẹ có một cuộc đoàn tụ “bất đắc dĩ” sau 10 năm xa cách. Cả hai vừa phải chịu đựng những thói hư tật xấu của nhau, vừa tìm cách hóa giải những mâu thuẫn trong quá khứ.', '2019-11-29', 90, 2, 'https://www.youtube.com/embed/l7qcDdI16pw?hd=1', 1, 'https://moveek.com/phim/anh-trai-yeu-quai/', 21, 7.1, 'Anh Trai Yeu Quai'),
(234, 'Người Cá Báo Thù', 'Mermaid Down', 'https://cdn.moveek.com/media/cache/short/5dcccd6189593642315108.jpg', 'Từ Đại Tây Dương, một người cá bị bắt, bị chặt đuôi, bị nhốt vào nhà thương điên. Chẳng ai tin cô ta là người cá.', '2019-11-22', 91, 4, 'https://www.youtube.com/embed/ELUVZCf7NcA?hd=1', 2, 'https://moveek.com/phim/mermaid-down/', 13, 7, 'Nguoi Ca Bao Thu'),
(235, 'Sát Nhân Trên Không', 'The Drone', 'https://cdn.moveek.com/media/cache/short/5dce5e7a2ace7287642030.jpg', 'Một cặp vợ chồng mới cưới bị khủng bố bởi một máy bay không người lái. Lần đầu tiên trên màn ảnh, một thiết bị điện tử tưởng chừng vô hại lại vô cùng đáng sợ, bạn sẽ không biết ai đang dõi theo bạn cũng như việc tiêu diệt nó thì không hề dễ dàng.', '2019-11-22', 82, 4, 'https://www.youtube.com/embed/eSibr5PqOIc?hd=1', 2, 'https://moveek.com/phim/the-drone/', 14, 4.1, 'Sat Nhan Tren Khong'),
(236, 'Tội Ác Hoàn Hảo', 'Angels Never Cry', 'https://cdn.moveek.com/media/cache/short/5dd622a7c56c7848873992.jpg', 'Theresa là một nữ họa sĩ tài năng, những gì cô muốn là được vẽ, nhưng không may cô ấy gặp tình trạng về mắt và sẽ trở nên mù lòa, thêm vào đó, cuộc hôn nhân không hạnh phúc, không có được sự ủng hộ từ chồng, Theresa bắt đầu gặp gỡ và làm quen với James, cũng là một nghệ sĩ, nhưng Theresa lại không biết nhiều về James và cả quá khứ đen tối của anh ta. Cô bắt đầu trở thành một nạn nhân theo kế hoạch của James…', '2019-11-22', 81, 4, 'https://www.youtube.com/embed/oybKbbYtowA?hd=1', 2, 'https://moveek.com/phim/angels-never-cry/', 12, 5.6, 'Toi ac Hoan Hao'),
(237, 'Flamenco Flamenco', 'Flamenco Flamenco', 'https://cdn.moveek.com/media/cache/short/5dd4c922cfacc152320017.jpg', 'Bài hát, điệu nhảy và âm nhạc của môn Flamenco.', '2019-11-22', 97, 0, '', 2, 'https://moveek.com/phim/flamenco-flamenco/', 12, 6.2, 'Flamenco Flamenco'),
(238, 'Những Đứa Con Của Hải Thú', 'Children of the Sea', 'https://cdn.moveek.com/media/cache/short/5dba5ab8015f1234024054.jpg', 'Ruka is a young girl whose parents are separated and whose father works in an aquarium. When two boys, Umi and Sora, who were raised in the sea by dugongs, are brought to the aquarium, Ruka feels drawn to them and begins to realize that she has the same sort of supernatural connection to the ocean that they do. Umi and Sora&#039;s special power seems to be connected to strange events that have been occurring more and more frequently, such as the appearance of sea creatures far from their home territory and the disappearance of aquarium animals around the world. However, the exact nature of the boys&#039; power and of the abnormal events is unknown, and Ruka gets drawn into investigating the mystery that surrounds her new friends.', '2019-11-09', 110, 0, '', 2, 'https://moveek.com/phim/children-of-the-sea/', 12, 6.6, 'Nhung dua Con Cua Hai Thu'),
(245, 'Maria (và những người xung quanh)', 'Maria (And Everybody Else)', 'https://cdn.moveek.com/media/cache/short/5dd4c9abb710b626583601.jpg', 'Kể từ khi mẹ mất, María đã phải gánh vác công việc chăm sóc bố và các anh em. Là một người trách nhiệm và luôn sát sao với mọi việc, María luôn tự hào với vai trò trụ cột của gia đình mình. Chính vì vậy, sau khi nghe tin bố cô sẽ kết hôn với cô y tá Cachita, María đã có cảm giác như cả thế giới đang sụp đổ. Đã đến lúc cô cần dũng cảm để sống cuộc sống của riêng mình.', '2019-11-22', 90, 4, '', 2, 'https://moveek.com/phim/maria-y-los-demas/', 11, 0, 'Maria (va nhung nguoi xung quanh)'),
(246, 'Kẻ Đâm Lén', 'Knives Out', 'https://cdn.moveek.com/media/cache/short/5dd7e9aa589d3983579478.jpg', 'Một buổi tiệc sinh nhật linh đình tại nhà riêng của vị tác giả truyện trinh thám nổi tiếng Harlan Thrombey, nhưng rồi ngày vui ấy lại mở màn cho một thảm kịch gia đình bí ẩn khó lý giải. Cụ ông Harlan Thrombey, 85 tuổi, được phát hiện rằng đã tự sát trong phòng riêng ngay sau buổi tiệc.', '2019-11-29', 130, 3, 'https://www.youtube.com/embed/m9Wckc09WrI?hd=1', 1, 'https://moveek.com/phim/knives-out/', 15, 8.1, 'Ke dam Len'),
(247, '21 Cây Cầu', '21 Bridges', 'https://cdn.moveek.com/media/cache/short/5dc3a80bf0b5b240307698.jpg', 'Một loạt cảnh sát tại New York bị giết chết bí ẩn. Thanh tra Andre Davis xông pha vào cuộc và phát hiện ra những âm mưu khủng khiếp của một băng đảng khát máu. Để triệt hạ chúng, Andre Davis đã khẩn cầu chính quyền đóng cửa toàn bộ 21 cây cầu, khiến cả hòn đảo Manhattan - trái tim của New York trở thành tử địa, nội bất xuất ngoại bất nhập.', '2019-11-29', 99, 4, 'https://www.youtube.com/embed/Z5I521xJvks?hd=1', 2, 'https://moveek.com/phim/21-bridges/', 11, 6.6, '21 Cay Cau'),
(248, 'Ford v. Ferrari: Cuộc Đua Lịch Sử', 'Ford v. Ferrari', 'https://cdn.moveek.com/media/cache/short/5dc5314942635089556489.jpg', 'Cuộc Đua Lịch Sử lấy bối cảnh đường đua Le Mans 1966, kể về cuộc cạnh tranh gắt gao giữa hai hãng xe hàng đầu thế giới. Trước sự thống trị của Ferrari, hãng Ford quyết định chiêu mộ nhóm kỹ sư do huyền thoại Carroll Shelby đứng đầu, cùng tay đua kiệt xuất Ken Miles để chế tạo ra một mẫu xe đua mang tính cách mạng nhằm đánh bại đối thủ Ferrari.', '2019-11-29', 152, 1, 'https://www.youtube.com/embed/fEgVkHU-sa8?hd=1', 2, 'https://moveek.com/phim/ford-v-ferrari/', 11, 8.3, 'Ford v. Ferrari: Cuoc dua Lich Su'),
(249, 'ELCANO và MAGELLAN: Hành Trình Bốn Bể', 'Elkano, lehen mundu bira', 'https://cdn.moveek.com/media/cache/short/5dd262726d2e7339670956.jpg', 'Chuyến thám hiểm của Elcano cùng  các thủy thủ đoàn đầy vui nhộn và đối đầu với thách thức của thiên nhiên… Tháng 9 này cùng Elcano giương buồm ra khơi đến các vùng đất mới', '2019-11-29', 90, 1, 'https://www.youtube.com/embed/MDebBQKnSzI?hd=1', 2, 'https://moveek.com/phim/elkano-lehen-mundu-bira/', 16, 0, 'ELCANO va MAGELLAN: Hanh Trinh Bon Be'),
(250, 'Giáng Sinh Năm Ấy', 'Last Christmas', 'https://cdn.moveek.com/media/cache/short/5dcb83d4e36a9453367354.jpg', 'Hai nhân vật cô đơn đang gặp phải những khó khăn trở ngại trong cuộc sống, và họ vô tình tìm thấy nhau khi thời khắc tuyệt vời nhất của năm – lễ Giáng Sinh đang cận kề.', '2019-12-06', 103, 2, 'https://www.youtube.com/embed/zYZ9MAMSHNw?hd=1', 1, 'https://moveek.com/phim/last-christmas/', 13, 6.6, 'Giang Sinh Nam ay'),
(251, 'Oppa, Phiền Quá Nha!', 'You and I', 'https://cdn.moveek.com/media/cache/short/5dbc24f4ab9a1524420633.jpg', 'Oppa, Phiền Quá Nha! là câu chuyện về Ca Dao, một nữ nhân viên văn phòng bình thường, chán nản với cuộc sống hiện tại và luôn chìm đắm trong những giấc mơ mà ở đó cô được là ca sĩ. Và cuộc đời Ca Dao bỗng chốc thay đổi khi cô nàng vô tình chạm mặt Chan Y - một ngôi sao đình đám của Hàn Quốc khi anh ta vừa đến Việt Nam.', '2019-12-06', 101, 3, 'https://www.youtube.com/embed/WSidWt35qmA?hd=1', 2, 'https://moveek.com/phim/oppa-phien-qua-nha/', 15, 4.6, 'Oppa, Phien Qua Nha!'),
(252, 'Kẻ Du Hành Trên Mây', 'The Aeronauts', 'https://cdn.moveek.com/media/cache/short/5dd2629fec029954243660.jpg', 'Chuyến hành trình, cuộc chiến sống còn trên khinh khí cầu, vươn tới độ cao kỷ lục hơn 10,000m chưa ai từng đạt được của nhà khoa học James Glaisher, thuộc viện Khoa học Hoàng Gia Anh cùng người cộng sự Amelia Wren - nữ phi công điều khiển khinh khí cầu chuyên nghiệp hiếm hoi của Vương quốc Anh Thế kỷ 19.', '2019-12-06', 100, 2, 'https://www.youtube.com/embed/MsnYCki6ksg?hd=1', 2, 'https://moveek.com/phim/the-aeronauts/', 12, 6.6, 'Ke Du Hanh Tren May'),
(253, 'Công Chúa Nhỏ Của Bà', 'A Litte Princess', 'https://cdn.moveek.com/media/cache/short/5ddb444587fe7302719618.jpg', 'Cặp đôi bà cháu vô tình gặp lại nhau sau những tổn thương mà cả hai phải chịu đựng trong cuộc sống. Khi bà Mal-soon phải sống cô độc vì người con gái bỏ đi thì Gong-joo lại phải sống vai của một người mẹ khi chỉ là một đứa trẻ. Mẹ mất để lại một đứa em gái còn quá nhỏ, Gong-joo tìm về với bà ngoại của mình là Mal-soon. Cuộc sống của ba con người xa lạ từ những giọt nước mắt bỗng tươi vui với nhiều tiếng cười.', '2019-12-06', 104, 1, 'https://www.youtube.com/embed/idJGKBeLUeA?hd=1', 2, 'https://moveek.com/phim/a-litte-princess/', 15, 0, 'Cong Chua Nho Cua Ba'),
(254, 'Ông Bạn Găng-Tơ', 'Man of Men', 'https://cdn.moveek.com/media/cache/short/5dd6a4d2ee4e4211256786.jpg', 'Ông Bạn Găng-Tơ kể về tay giang hồ hay khoe mẽ Young-Ki với ước mơ có một cuộc sống “đỉnh cao” bằng cách thường xuyên chơi vé số trực tuyến.Trong một lần quậy phá, gã bị tòa tuyên án 150 giờ lao động công ích. Nhiệm vụ của Young-Ki chính là chăm sóc cho vị Jang-Soo - một vị luật sư nổi tiếng và giàu có nay lại bại liệt toàn thân và sắp chết sau cơn bạo bệnh.', '2019-12-06', 116, 4, 'https://www.youtube.com/embed/W1oHZAuhZOU?hd=1', 2, 'https://moveek.com/phim/man-of-men/', 11, 5.5, 'ong Ban Gang-To'),
(255, 'Trò Chơi Kỳ Ảo: Thăng Cấp', 'Jumanji: The Next Level', 'https://cdn.moveek.com/media/cache/short/5de07b4ec02e5268652699.jpg', 'Khi những người chơi quay lại, trò chơi đã thay đổi. Trở về Jumanji để giải cứu bạn mình, họ phát hiện mọi thứ không còn như mong đợi. Người chơi sẽ phải dũng cảm đối mặt với những màn chơi ẩn mật chưa từng được khám phá, từ sa mạc khô cằn đến những ngọn núi tuyết, để tìm thấy lối thoát khỏi trò chơi ảo nguy hiểm nhất thế giới...', '2019-12-13', 123, 2, 'https://www.youtube.com/embed/X9dlkZ-arhc?hd=1', 1, 'https://moveek.com/phim/jumanji-the-next-level/', 18, 7, 'Tro Choi Ky ao: Thang Cap'),
(256, 'Khủng Long Ăn Chay', 'My TYRANO: Together, Forever', 'https://cdn.moveek.com/media/cache/short/5dce1f236b12c884842459.jpg', 'Khi thế giới bước vào Kỷ Băng Hà, sự sống của loài khủng long bị đe dọa nghiêm trọng. Khí hậu trở nên khắc nghiệt và thức ăn vô cùng khan hiếm. Punon là một cô khủng long Pteranodon bé nhỏ đang trên đường kiếm tìm vùng đất Thiên Đường – nơi an toàn nhất cho khủng long ăn cỏ và có rất nhiều Quả Thần – loài quả màu đỏ có hương vị thơm ngon, thì Khủng Long Bạo Chúa Tyrannosaurus – loài khủng long ăn thịt nguy hiểm nhất hành tinh xuất hiện!', '2019-12-13', 0, 0, 'https://www.youtube.com/embed/uKJsJ9fQkeU?hd=1', 1, 'https://moveek.com/phim/my-tyrano-together-forever/', 18, 0, 'Khung Long an Chay'),
(257, 'Giáng Sinh Đen', 'Black Christmas', 'https://cdn.moveek.com/media/cache/short/5ddf4c24c407a853232775.jpg', 'Trường Đại học Hawthorne trở nên vắng lặng hơn khi bước vào kì nghỉ lễ. Tuy nhiên, Riley Stone và chị em nhà Kappa, Epsilon – Marty năng động, Kris nổi loạn  và Jesse ham ăn – lại tưng bừng tiệc tùng, và bị một kẻ đeo mặt nạ đen theo dõi và bắt đầu kế hoạch sát hại lần lượt từng người trong nhóm.', '2019-12-13', 92, 4, 'https://www.youtube.com/embed/VzxsNBDPJNg?hd=1', 1, 'https://moveek.com/phim/black-christmas/', 14, 3.2, 'Giang Sinh den'),
(258, 'Bạn Âm 2: Ngôi Nhà Của Quỷ', 'Danur 2: Maddah', 'https://cdn.moveek.com/media/cache/short/5ddb83f9347ce621757889.jpg', 'Risa bắt gặp chú mình đang đi với một người phụ nữ là và nghi ngờ chú đang ngoại tình. Risa ra quyết định tự điều tra một mình. Nhưng sau khi nghe được câu em họ tiết lộ những chuyện kỳ lạ đang xảy ra trong gia đình và gặp phải linh hồn của một người phụ nữ trong căn nhà, Risa tự hỏi liệu chú mình có thật sự ngoại tình hay không?', '2019-12-13', 92, 0, 'https://www.youtube.com/embed/zySgaXchLoM?hd=1', 2, 'https://moveek.com/phim/danur-2-maddah/', 19, 5.6, 'Ban am 2: Ngoi Nha Cua Quy'),
(259, 'Chị Chị Em Em', 'Midnight Confession', 'https://cdn.moveek.com/media/cache/short/5dcb6f01b9ad0228639656.jpg', 'Chị Chị Em Em đặt Thiên Kim và Bảo Nhi vào một trò chơi yêu hận tình thù mà chỉ có một người được thắng, khi hạnh phúc của tổ ấm nhỏ sắp bị bão tố cuốn phăng, liệu họ sẽ quyết liệt đấu tranh giành lấy điều mình muốn hay sẽ cam chịu hi sinh để giữ lấy sự ấm êm?', '2019-12-20', 104, 4, 'https://www.youtube.com/embed/IJHTCjwbETM?hd=1', 1, 'https://moveek.com/phim/chi-chi-em-em/', 19, 3.9, 'Chi Chi Em Em'),
(260, 'Star Wars IX: Skywalker Trỗi Dậy', 'Star Wars: The Rise of Skywalker', 'https://cdn.moveek.com/media/cache/short/5daed2712984e675931163.jpg', 'Skywalker Trỗi Dậy, lấy bối cảnh một năm sau sự kiện của Jedi Cuối Cùng, khi phe Kháng chiến tẩu thoát trong gang tất trước sự bao vây của First Order.', '2020-01-03', 0, 0, 'https://www.youtube.com/embed/Yb67ukdDPbE?hd=1', 3, 'https://moveek.com/phim/star-wars-the-rise-of-skywalker/', 10, 0, 'Star Wars IX: Skywalker Troi Day'),
(261, 'Mắt Biếc', 'Mắt Biếc', 'https://cdn.moveek.com/media/cache/short/5ddf3d5699dbb733816535.jpg', 'Chuyện tình đơn phương của chàng thanh niên Ngạn dành cho cô bạn từ thuở nhỏ Hà Lan... Ngạn và Hà Lan vốn là hai người bạn từ thuở nhỏ, cùng ở làng Đo Đo an bình. Họ cùng nhau đi học, cùng trải qua quãng đời áo trắng ngây thơ vụng dại với những cảm xúc bồi hồi của tuổi thiếu niên.', '2019-12-20', 117, 3, 'https://www.youtube.com/embed/b_OUi6OquTU?hd=1', 1, 'https://moveek.com/phim/mat-biec/', 16, 7.5, 'Mat Biec'),
(263, 'Rambo: Hồi Kết Đẫm Máu', 'Rambo: Last Blood', 'https://cdn.moveek.com/media/cache/short/5de4c926aff5c472791707.jpg', 'Hồi Kết Đẫm Máu sẽ tiếp nối hành trình sử thi của Rambo khi ông đã dần ổn định, tận hưởng cuộc sống bình lặng bên gia đình tại một trang trại ở Arizona. Mọi chuyện đều đang diễn ra tốt đẹp cho đến khi Gabriela hay tin về cha ruột của mình và cô quyết định đi tìm người cha tệ bạc ấy để hỏi rõ lý do vì sao năm xưa ông đã bỏ rơi cô. Mặc cho Maria và Rambo ngăn cản quyết liệt, cô gái trẻ vẫn bất chấp sang Mexico.', '2019-12-24', 0, 0, 'https://www.youtube.com/embed/Yl6Ao2nvisI?hd=1', 1, 'https://moveek.com/phim/rambo-last-blood/', 11, 6.3, 'Rambo: Hoi Ket dam Mau'),
(264, 'Cats: Những Chú Mèo', 'Cats', 'https://cdn.moveek.com/media/cache/short/5df853cfabe88641185705.jpg', 'Trong một đêm quan trọng, bộ tộc mèo có tên Jellicles quyết định con mèo nào sẽ được gửi xuống Heaviside Layer để hồi sinh.', '2019-12-24', 0, 0, 'https://www.youtube.com/embed/TUYt1s0Sj_w?hd=1', 1, 'https://moveek.com/phim/cats/', 11, 2.6, 'Cats: Nhung Chu Meo'),
(265, 'Điệp Viên Ẩn Danh', 'Spies in Disguise', 'https://cdn.moveek.com/media/cache/short/5d99c7f64b526664126543.jpg', 'Biệt đội giải cứu thế giới của siêu điệp viên Lance Sterling và các phụ tá siêu tài năng và thông minh!', '2019-12-27', 111, 1, 'https://www.youtube.com/embed/tFuoQgbllVs?hd=1', 3, 'https://moveek.com/phim/spies-in-disguise/', 11, 0, 'diep Vien an Danh'),
(267, 'Lời Nguyền', 'Grudge', 'https://cdn.moveek.com/media/cache/short/5db7c7a0ec28d382404735.jpg', 'Một ác ma đầy oán hận nguyền rủa cái chết khủng khiếp nhất đền bất kỳ kẻ nào tiến vào ngôi nhà của nó.', '2020-01-03', 0, 0, 'https://www.youtube.com/embed/iyHXilCkWww?hd=1', 3, 'https://moveek.com/phim/the-grudge-1/', 10, 0, 'Loi Nguyen'),
(268, 'One Piece: Lễ Hội Hải Tặc', 'One Piece: Stampede', 'https://cdn.moveek.com/media/cache/short/5de71aba48234919116867.jpg', 'Luffy và các thành viên băng Mũ Rơm nhận được thư mời đến &quot;Lễ hội Hải tặc&quot; từ người tổ chức Buena Festa. Và vô số Hải tặc khắp nơi trên thế giới cũng đã đến đây để cùng hướng về một vũ đài duy nhất &quot;Tranh đoạt chiếc chìa khóa dẫn đến kho báu Vua Hải Tặc (Roger)&quot;. Nhưng điều bất ngờ nhất chính là Douglas Bullet - tên quái vật với biệt danh &quot;Hậu duệ của quỷ&quot; cũng đột ngột tham chiến. Và đội ngũ Hải Quân cũng đã bí mật tham gia với mục đích tóm gọn các Hải tặc.', '2020-01-03', 0, 0, 'https://www.youtube.com/embed/qmpk8JJjjmQ?hd=1', 3, 'https://moveek.com/phim/one-piece-stampede/', 10, 0, 'One Piece: Le Hoi Hai Tac'),
(271, 'Kẻ Săn Mồi Biển Sâu', 'Underwater', 'https://cdn.moveek.com/media/cache/short/5dd7ea3d8788c765167371.jpg', 'Kẻ Săn Mồi Biển Sâu xoay quanh một nhóm kĩ sư dầu khí làm việc tại dàn khoan, trong một nhiêm vụ khắc phục sự cố tại chân của dàn khoan ở đáy biển, nhóm kĩ sư phát hiện tính mạng của bản thân bị đe doạ bởi một sinh vật bí ẩn.', '2020-01-10', 0, 0, 'https://www.youtube.com/embed/wuSYtsviREU?hd=1', 3, 'https://moveek.com/phim/underwater/', 10, 0, 'Ke San Moi Bien Sau'),
(275, 'Clara và Khu Rừng Kỳ Bí', 'Clara', 'https://cdn.moveek.com/media/cache/short/5ddcb35e5745e223197746.jpg', 'Ngày xửa ngày xưa, một chú rồng ma thuật được sinh ra ở vùng đất cổ tích. Vì sự hiếm có đặc biệt của mình, chú bị bắt cóc bởi những thế lực xấu xa. Tuy nhiên, nhóm người xấu vô tình đánh mất chú rồng con, và được “cứu rỗi” bởi một anh gấu chồn và một chú lùn.', '2019-12-06', 85, 1, 'https://www.youtube.com/embed/LJwNcKg4AYU?hd=1', 2, 'https://moveek.com/phim/clara/', 14, 6.2, 'Clara va Khu Rung Ky Bi'),
(276, 'Hài Đỏ và 7 Chú Lùn', 'Red Shoes &amp; the 7 Dwarfs', 'https://cdn.moveek.com/media/cache/short/5de9bdc080aab982787152.jpg', 'Các chàng hoàng tử bị phù phép thành chú lùn, phải đi tìm đôi hài đỏ của một nàng công chúa để hóa giải lời nguyền.', '2019-12-27', 0, 1, 'https://www.youtube.com/embed/_SIBtK3UgUc?hd=1', 3, 'https://moveek.com/phim/red-shoes-the-7-dwarfs/', 10, 0, 'Hai do va 7 Chu Lun'),
(277, 'Cuộc Giải Cứu Hang Tham Luang', 'The Cave', 'https://cdn.moveek.com/media/cache/short/5deb37498327a743527173.jpg', 'Chiến dịch phi thường giải cứu đội bóng nhí Thái Lan &quot;Lợn Hoang&quot; chấn động cả Thế giới mùa hè năm 2018. 18 thợ lặn nòng cốt đã vượt qua hành trình tử thần hơn 10km để làm nên kỳ tích - cứu sống 12 cầu thủ nhí sau 10 ngày mắc kẹt trong bóng tối, không thức ăn, không nước sạch.', '2020-01-03', 0, 0, 'https://www.youtube.com/embed/845EM5Z5Hxc?hd=1', 3, 'https://moveek.com/phim/the-cave/', 11, 0, 'Cuoc Giai Cuu Hang Tham Luang'),
(278, 'Bẫy Quỷ', 'In the Trap', 'https://cdn.moveek.com/media/cache/short/5ddf8c7d7bcc4981113321.jpg', 'Philip, vốn là một biên tập viên đơn độc với quá khứ đầy ám ảnh khi em gái của anh bị mất tích một cách man rợ. Giam mình trong suốt 2 năm trời, anh bị những thế lực ma quỷ hành hạ từ ngày ngày qua ngày khác. Những sức mạnh đen tối đã dùng chính những người thân yêu của anh để khiến anh phải mất hết tất cả.', '2019-12-06', 88, 4, 'https://www.youtube.com/embed/O06UaYhmfVo?hd=1', 2, 'https://moveek.com/phim/in-the-trap/', 12, 6.7, 'Bay Quy');
INSERT INTO `movie` (`movie_id`, `name_vi`, `name_en`, `poster`, `description`, `release_date`, `duration`, `rated_id`, `trailer`, `type_id`, `url`, `view`, `imdb`, `name_search`) VALUES
(280, 'Đại Thảm Họa Núi Baekdu', 'Baekdu Mountain', 'https://cdn.moveek.com/media/cache/short/5df32e7023f87890059145.jpg', 'Núi Baekdu – ngọn núi lửa cao nhất bán đảo Triều Tiên nằm ở biên giới giữa Triều Tiên và Trung Quốc bất ngờ phun trào, khiến toàn bộ người dân trên bán đảo kinh hoàng. Thảm họa chưa dừng lại ở đó, tiếp theo sau sẽ là những lần phun trào được dự báo là dữ dội hơn nữa, đe dọa sẽ nhấn chìm toàn bộ bán đảo Triều Tiên trong tro tàn tận thế.', '2020-01-03', 0, 0, 'https://www.youtube.com/embed/elGNYVBx0Uc?hd=1', 3, 'https://moveek.com/phim/baekdu-mountain/', 10, 0, 'dai Tham Hoa Nui Baekdu'),
(281, 'Chồng Cũ, Tình Mới', 'Love, Again', 'https://cdn.moveek.com/media/cache/short/5de5fc9b3d65f747346516.jpg', 'Sau buổi ly hôn, Hyun-woo và Sun-young tưởng chừng sẽ có cuộc sống riêng. Nhưng chưa kịp tận hưởng đời độc thân, cuộc sống của Hyun-woo lại bị đảo lộn khi vợ cũ bất ngờ xuất hiện cùng người đàn ông đang theo đuổi cô - Sang-cheol, vốn là bạn từ thời trung học của Hyun-woo.', '2019-12-27', 112, 0, 'https://www.youtube.com/embed/Gxsuvc1p9-Y?hd=1', 3, 'https://moveek.com/phim/love-again/', 10, 0, 'Chong Cu, Tinh Moi'),
(282, 'Những Gã Hư Trọn Đời', 'Bad Boys for Life', 'https://cdn.moveek.com/media/cache/short/5d95747513d51953102207.jpg', 'Những Gã Hư Trọn Đời xoay quanh nhiệm vụ mới của Mike và Marcus. Sở cảnh sát Miami có một đội ngũ ưu tú mới tên là AMMO. Họ được giao nhiệm vụ hạ gục một thủ lĩnh băng đảng ma túy tên là Armando  Nhân vật phản diện này được mô tả là một kẻ giết người máu lạnh với bản tính hung ác.', '2020-01-17', 0, 0, 'https://www.youtube.com/embed/NRcLg0g7ZKY?hd=1', 3, 'https://moveek.com/phim/bad-boys-for-life/', 10, 0, 'Nhung Ga Hu Tron doi'),
(284, 'Bác Sĩ DOLITTLE: Chuyến Phiêu Lưu Thần Thoại', 'Dolittle', 'https://cdn.moveek.com/media/cache/short/5dd34853b3c5a717601438.jpg', 'Bác sĩ nổi tiếng Dolittle - người đàn ông có thể nói chuyện với động vật. Sau khi vợ mất, anh sống tách biệt với thế giới bên ngoài, chỉ đến khi nữ hoàng Anh bị bệnh, anh mới trở lại. Dolittle đã dấn thân trong hành trình phiêu lưu đầy nguy hiểm đến hòn đảo thần thoại để tìm ra phương thuốc chữa bệnh.', '2020-01-25', 0, 0, 'https://www.youtube.com/embed/lWqtKbQpyeM?hd=1', 3, 'https://moveek.com/phim/dolittle/', 10, 0, 'Bac Si DOLITTLE: Chuyen Phieu Luu Than Thoai'),
(285, '30 Chưa Phải Tết', '30 Chưa Phải Tết', 'https://cdn.moveek.com/media/cache/short/5dd6a88d6d681791093001.jpg', 'Hân mồ côi mẹ từ nhỏ, sống với ba và thường xuyên bị đánh đập nên Hân hận ba, rời bỏ quê lên thành phố lập nghiệp. Sau 12 năm, Hân về quê và &quot;giành&quot; miếng đất của chính ba mình nhằm có thể cưới con gái của ông chủ tập đoàn bất động sản lớn nhất Sài Gòn nhưng vô tình anh bị rơi vào vòng lập thời gian vô tận khi phải sống lập đi lập lại trong đúng ngày 30 Tết.', '2020-01-25', 0, 0, '', 3, 'https://moveek.com/phim/30-chua-phai-la-tet/', 10, 0, '30 Chua Phai Tet'),
(289, 'Gái Già Lắm Chiêu 3', '', 'https://cdn.moveek.com/media/cache/short/5de3ca5c52888382795701.jpg', 'Thái Tuyết Mai là một cựu minh tinh màn bạc từng gây bao sóng gió, thị phi trong quá khứ đã “quy ẩn giang hồ” bất ngờ quay trở lại. Còn Ms Q là nữ hoàng thị phi “đang lên” với chương trình chuyên phanh phui bí mật, thị phi, scandal trong showbiz. Hai “gái già lắm chiêu” trực tiếp đối đầu nhau với quan hệ “Mẹ chồng siêu giàu – Nàng dâu siêu chảnh” hứa hẹn sẽ mang nhiều điều thú vị và bất ngờ.', '2020-02-07', 0, 0, '', 3, 'https://moveek.com/phim/gai-gia-lam-chieu-3/', 10, 0, 'Gai Gia Lam Chieu 3'),
(290, 'The Loud House', 'The Loud House', 'https://cdn.moveek.com/bundles/ornweb/img/no-poster.png', 'Lincoln Loud is an eleven-year-old boy who lives with ten sisters. With the help of his right-hand man Clyde, Lincoln finds new ways to survive in such a large family every day.', '2020-02-05', 0, 0, '', 3, 'https://moveek.com/phim/the-loud-house/', 10, 0, 'The Loud House'),
(291, 'Birds of Prey', 'Birds of Prey', 'https://cdn.moveek.com/media/cache/short/5d82d7fd9a220586123308.jpg', 'Birds of Prey sẽ tiếp nối những sự kiện xảy ra trong Suicide Squad, khi Harley Quinn không còn phụ thuộc vào Joker và đang tìm đường hoàn lương. Giữa một thành phố Gotham hỗn loạn sau sự biến mất của người hùng bóng đêm Gotham, cô bé Cassandra Cain tìm thấy một viên kim cương vốn thuộc về gã ác nhân Black Mask và bị tên này bắt giữ. Khi đó, Harley Quinn đã tập hợp các nữ anh hùng để lên đường giải cứu Cassandra.', '2020-02-07', 0, 0, 'https://www.youtube.com/embed/MIQdlY2wkL4?hd=1', 3, 'https://moveek.com/phim/birds-of-prey/', 10, 0, 'Birds of Prey'),
(292, 'Bí Mật Của Gió', 'Secrets of the Wind', 'https://cdn.moveek.com/media/cache/short/5d9dc25fc435c117042811.jpg', 'Linh là một cô bé học sinh có năng khiếu vẽ. Để tạo thuận lợi cho con gái theo học nghệ thuật, bố mẹ Linh quyết định bán tài sản ở quê để chuyển đến một căn biệt thự cổ tại Đà Lạt sinh sống. Lúc đó có mơ Linh cũng không thể tưởng tượng cô phải chia sẻ phòng ngủ của mình với một người lạ, là nam giới và đặc biệt người này lại đến từ thế giới bên kia.', '2020-02-07', 0, 0, 'https://www.youtube.com/embed/QeL4Y2v-I5A?hd=1', 3, 'https://moveek.com/phim/bi-mat-cua-gio/', 10, 0, 'Bi Mat Cua Gio'),
(294, 'Hãy Nhảy Cùng Tôi', 'Dance with Me', 'https://cdn.moveek.com/media/cache/short/5dba5dabac9da003528135.jpg', 'Shizuka có quan hệ với ông chủ đẹp trai, quyến rũ nhưng không biết tương lai của mối tình này đi đến đâu nên đã quyết định đi gặp thầy bói xem mình kết hôn được hay không. Nhưng do sự nhầm lẫn, thầy bói đã đặt lời nguyền khiến cho Shizuka nhảy bất cứ khi nào cô nghe tiếng nhạc. Một cuộc họp quan trọng sắp diễn ra, Shizuka sẽ phải làm cách nào để phá bỏ lời nguyền?', '2019-11-12', 0, 0, '', 2, 'https://moveek.com/phim/dance-with-me/', 10, 6.9, 'Hay Nhay Cung Toi'),
(295, 'Liên Minh Thú Cưng', 'Pets United', 'https://cdn.moveek.com/bundles/ornweb/img/no-poster.png', 'Khi cặp đôi chó oan gia ROGER và BELLE phải cùng chiến đấu chống lại sự tấn công của lũ rô bốt vào thành phố.', '2020-01-25', 106, 0, 'https://www.youtube.com/embed/LZjJLHAH-Vo?hd=1', 3, 'https://moveek.com/phim/pets-united/', 10, 0, 'Lien Minh Thu Cung'),
(296, 'Những Chàng Samurai Tháo Vát', 'Samurai Shifters', 'https://cdn.moveek.com/media/cache/short/5dba5b0d34f83181990993.jpg', 'Chuyện kể về một samurai mọt sách vào thời Edo tên là Shunnosuke Katagiri. Anh ấy là một người khép kín, ngây thơ và ít trải nghiệm. Một ngày nọ, anh được chỉ định là người phụ trách việc thuyên chuyển cả đại gia tộc đến một vùng khác.', '2019-11-09', 120, 2, '', 2, 'https://moveek.com/phim/samurai-shifters/', 10, 6.7, 'Nhung Chang Samurai Thao Vat'),
(302, 'Hợp Đồng Bán Mình', '', 'https://cdn.moveek.com/media/cache/short/5dba5f326e42c814988911.jpg', 'Một người đàn ông đại gia Việt kiều U60 dám bỏ ra 120 tỷ để cứu cha, một cô gái trẻ đang đối diện với cái chết và nợ nần với điều kiện cô gái ấy phải làm vợ ông ta trong 10 năm không được ly hôn. Bản hợp đồng hôn nhân mà cô gái ấy phải nhắm mắt ký vào đã biến cô trở thành một nàng Kiều thời @...', '2019-11-05', 0, 0, '', 2, 'https://moveek.com/phim/hop-dong-ban-minh/', 11, 0, 'Hop dong Ban Minh'),
(303, 'Trẻ Trâu Khởi Nghiệp', 'Start-Up', 'https://cdn.moveek.com/media/cache/short/5df7250dece46554310618.jpg', 'Những tưởng đường đời sẽ dễ thở hơn trường học, đôi bạn Taek-Il và Sang-Pil quyết nghỉ học để dấn thân vào đời. Từ đây hai thanh niên &quot;trẻ trâu&quot; mới có cơ hội nếm vị trưởng thành sau những lần bị đời vùi dập.', '2019-12-27', 0, 0, 'https://www.youtube.com/embed/7V4QdM1_8RI?hd=1', 3, 'https://moveek.com/phim/start-up/', 10, 0, 'Tre Trau Khoi Nghiep'),
(304, 'Đôi Mắt Âm Dương', 'Đôi Mắt Âm Dương', 'https://cdn.moveek.com/media/cache/short/5df413e2918f5296306491.jpg', 'Một gia đình giàu có nhưng đang bị một bí mật bao trùm khi người vợ bỗng dưng có được đôi mắt nhìn thấy ma sau tai nạn. Vì mất trí nhớ nên cô được chồng mình đưa về nhà để tái hòa nhập cuộc sống. Thế nhưng, có vẻ như mọi thứ xung quanh cô đều bất thường. Từ bà giúp việc với giọng điệu mờ ám đến ánh mắt bí hiểm của ông quản gia và hình ảnh con quạ mang theo những điềm báo chết chóc.', '2020-01-25', 0, 3, 'https://www.youtube.com/embed/TYdcN0dSrNA?hd=1', 3, 'https://moveek.com/phim/doi-mat-am-duong/', 10, 0, 'doi Mat am Duong'),
(307, 'Bệnh Nhân &quot;Củ Chuối&quot;', 'A Banana? At This Time of Night?', 'https://cdn.moveek.com/media/cache/short/5dba5ce54a93f326318527.jpg', 'Yasuaki Shikano, 34 tuổi, mắc chứng bại liệt. Mặc dù phải ngồi xe lăn, anh ta vẫn quyết rời bệnh viện và tự lập cuộc sống theo cách không giống ai. Anh ta tuyển các tình nguyện viên về phục vụ cho mình. Dù có mè nheo cỡ nào thì anh ta vẫn khiến mọi người phải chiều theo ý thích riêng dù là nhất thời. Đặc biệt là với cậu sinh viên Y khoa Tanaka, anh ta liên tục.', '2019-11-10', 0, 2, '', 2, 'https://moveek.com/phim/a-banana-at-this-time-of-night/', 10, 0, 'Benh Nhan &quot;Cu Chuoi&quot;'),
(309, 'Hai Phượng', 'Furie', 'https://cdn.moveek.com/media/cache/short/5c3d614e46d19408073352.jpg', 'Hai Phượng là hành trình nghẹt thở và căng thẳng của bà mẹ đơn thân Hai Phượng khi phải đối đầu với cả 1 đường dây tội phạm bắt cóc và buôn bán nội tạng xuyên quốc gia để cứu đứa con gái bé bỏng Mai. Để cứu được con, Hai Phượng chỉ có 14 tiếng đồng hồ,  bất kỳ một sơ xuất nhỏ nào cũng sẽ phải trả giá bằng chính mạng sống của chính cô và bé Mai.', '2019-02-22', 100, 4, 'https://www.youtube.com/embed/XUSjnQmuc-M?hd=1', 1, 'https://moveek.com/phim/hai-phuong/', 10, 6.1, 'Hai Phuong'),
(310, 'Cua Lại Vợ Bầu', 'Cua Lai Vo Bau', 'https://cdn.moveek.com/media/cache/short/5c3d4dafd255d392786021.jpg', 'Chuyện tình của Trọng Thoại và Nhã Linh đang hoàn hảo cho đến khi người yêu cũ của Nhã Linh là Quý Khánh thình lình xuất hiện và gây nên hàng loạt xáo trộn trong trái tim cô gái.', '2019-02-05', 102, 3, 'https://www.youtube.com/embed/vfQsaG5FkN4?hd=1', 1, 'https://moveek.com/phim/cua-lai-vo-bau/', 11, 6.2, 'Cua Lai Vo Bau'),
(311, 'Song Lang', 'Song Lang', 'https://cdn.moveek.com/media/cache/short/3552a564d5a0f5b99b061fea90160949542dace7.jpg', 'Song Lang lấy bối cảnh vào những năm 70 - 80 của thế kỷ trước, xoay quanh những câu chuyện về đoàn cải lương Thiên Lý.', '2018-08-17', 101, 3, 'https://www.youtube.com/embed/LR_wkRmNQb0?hd=1', 1, 'https://moveek.com/phim/song-lang/', 11, 8, 'Song Lang'),
(312, 'Quái Vật Nguyên Sinh', 'Primal', 'https://cdn.moveek.com/media/cache/short/5dfa30c065d54249589014.jpg', 'Khi một thợ săn, một kẻ sát nhân và những con quái thú chạm mặt nhau trên một chuyến tàu lênh đênh giữa đại dương, ai sẽ là người sống sót?', '2019-12-27', 97, 0, 'https://www.youtube.com/embed/uK1AazLiayg?hd=1', 3, 'https://moveek.com/phim/primal/', 10, 0, 'Quai Vat Nguyen Sinh'),
(313, 'Vụ Án Thế Kỷ', 'The Murder of Nicole Brown Simpson', 'https://cdn.moveek.com/media/cache/short/5dfa31ad3a840293910357.jpg', 'Lấy cảm hứng từ những sự kiện có thật, bộ phim theo chân O.J. Vợ cũ của Simpson Nicole Brown Simpson trong những ngày cuối cùng dẫn đến cái chết bi thảm của cô vào ngày 12 tháng 6 năm 1994.', '2019-12-27', 0, 0, 'https://www.youtube.com/embed/dKYcLKQpWVo?hd=1', 3, 'https://moveek.com/phim/the-murder-of-nicole-brown-simpson/', 10, 0, 'Vu an The Ky'),
(314, 'Trận Chiến Midway', 'Midway', 'https://cdn.moveek.com/media/cache/short/5da877d91b4d5405746477.jpg', 'Trận Chiến Midway lấy bối cảnh trong Thế Chiến thứ 2 sau sự kiện Trân Châu Cảng nổi tiếng, tái hiện trận đánh lịch sử Midway - điểm nóng quan trọng tại Thái Bình Dương', '2020-01-10', 0, 0, 'https://www.youtube.com/embed/0IzKfDq9aHI?hd=1', 3, 'https://moveek.com/phim/midway/', 10, 0, 'Tran Chien Midway'),
(315, 'Anh Thầy Ngôi Sao', 'Superstar Teacher', 'https://cdn.moveek.com/media/cache/short/5d390d2e76e69752749151.jpg', 'Anh Thầy Ngôi Sao xoay quanh Hoàng – một chàng trai trẻ khao khát trở thành ca sĩ nổi tiếng, vì chưa gặp thời nên tạm kiếm sống bằng công việc làm thầy giáo dạy nhạc. Sau một biến cố, Hoàng buộc phải chuyển công tác ra đảo Quí – hòn đảo nhỏ biệt lập cách xa đất liền – và gặp không ít phiền toái khi tiếp quản một lớp học đặc biệt chỉ với vỏn vẹn 5 học trò. Nhiều lần bỏ về không thành vì đám nhỏ và người dân trên đảo luôn tìm mọi cách để giữ chân thầy giáo, Hoàng vừa miễn cưỡng chấp nhận cuộc sống mới, vừa tìm cơ hội thực hiện ước mơ của mình.', '2019-08-30', 107, 1, 'https://www.youtube.com/embed/jveh2qTKq5U?hd=1', 2, 'https://moveek.com/phim/anh-thay-ngoi-sao/', 10, 6.2, 'Anh Thay Ngoi Sao'),
(316, 'Ký Sinh Trùng', 'Parasite', 'https://cdn.moveek.com/media/cache/short/5cf65cdf194ef571846527.jpg', 'Ký Sinh Trùng xoay quanh hai gia đình hoàn toàn trái ngược: một bên vô cùng giàu có, còn một bên luôn sống trong cảnh nghèo túng. Họ tình cờ gặp gỡ và quen biết nhau sau một biến cố không ngờ.', '2019-06-21', 132, 4, 'https://www.youtube.com/embed/gQBtd75imHE?hd=1', 2, 'https://moveek.com/phim/parasite/', 10, 8.6, 'Ky Sinh Trung'),
(317, 'Yêu Nhầm Bạn Thân', 'Friend Zone', 'https://cdn.moveek.com/media/cache/short/5c7568591e3cb605638655.jpg', 'Yêu Nhầm Bạn Thân xoay quanh mối tình đơn phương của Palm – một tiếp viên hàng không. Ngay từ lúc còn ngồi trên ghế nhà trường, anh đã thầm yêu Gink, nhưng sau đó, quyết định giữ tình bạn thân thiết. 10 năm trôi qua, cả hai trưởng thành và vẫn giữ mối quan hệ bạn thân khác giới, những khi bên nào có trục trặc với người yêu, thì đều có bên kia xuất hiện làm “phao cứu sinh”.', '2019-03-15', 118, 3, 'https://www.youtube.com/embed/Wt8VCmovYn0?hd=1', 2, 'https://moveek.com/phim/friend-zone/', 10, 7.3, 'Yeu Nham Ban Than'),
(318, 'Ván Cờ Sinh Tử', 'The Divine Move 2: The Wrathful', 'https://cdn.moveek.com/media/cache/short/5dfc44b95c92c880430610.jpg', 'Quỷ Thủ là người mất hết tất cả chỉ vì một ván cờ vây. Khi sư phụ Hur Il-do, người thầy mà anh tin tưởng nhất qua đời, anh quyết tâm vạch ra một kế hoạch trả thù những kẻ từng hãm hại mình. Bất chấp mọi hiểm nguy, Quỷ Thủ chu du khắp nơi, thách đấu những kỳ thủ cờ vây “máu mặt” và tạo nên những ván cờ đầy kịch tính và đẫm máu.', '2020-01-03', 106, 0, '', 3, 'https://moveek.com/phim/the-divine-move-2-the-wrathful/', 10, 0, 'Van Co Sinh Tu'),
(319, 'Căn Phòng Cám Dỗ', 'The Room', 'https://cdn.moveek.com/media/cache/short/5dfd7400210ec213752842.jpg', 'Sau khi chuyển đến căn biệt thự ở một khu vực hoang vu, vợ chồng Kate và Matt tìm được căn phòng bí mật có khả năng đáp ứng tất cả mong muốn: quần áo đắt tiền, tranh nghệ thuật của họa sĩ Van Gosh hay tiền chất đống thành núi,...Một ngày kia, Kate mong ước có con và từ đó họ đối diện với những hậu quả khó lường từ ước muốn ấy.', '2020-01-03', 100, 0, 'https://www.youtube.com/embed/9Wf9M8jZdTM?hd=1', 3, 'https://moveek.com/phim/the-room/', 10, 0, 'Can Phong Cam Do'),
(320, 'Thí Nghiệm Xác Sống', 'Patients Of A Saint', 'https://cdn.moveek.com/media/cache/short/5dfd74a100e1d027800568.jpg', 'Hòn đảo St. Leonards ở phía bắc đại dương là nơi giam giữ những tội phạm tàn bạo nhất thế giới. Tại đây, họ là vật thí nghiệm của những thí nghiệm y khoa khắc nghiệt. Khi cuộc thử nghiệm bắt đầu đi chệch hướng, cả hòn đảo biến thành một mê cung khổng lồ chứa đầy mầm bệnh mà những người sống sót đối mặt cận kề.', '2020-01-10', 0, 0, '', 3, 'https://moveek.com/phim/patients-of-a-saint/', 10, 0, 'Thi Nghiem Xac Song'),
(321, 'Bikeman 2: Ôm Hờ Yêu Thật', 'Bikeman 2', 'https://cdn.moveek.com/media/cache/short/5dfd74d62c94e323374457.jpg', 'Tiếp nối phần 1, Sakkarin giờ đây đã  có một công việc ổn định và vẫn yêu thầm cô bạn Jai. Để không bị ba ép buộc, Jai nhờ Sakkarin đóng giả làm bàn trai mình. Câu chuyện Friendzone một lần nữa được Thái Lan đưa lên màn ảnh rộng một cách khéo léo', '2020-01-10', 107, 0, '', 3, 'https://moveek.com/phim/bikeman-2/', 10, 0, 'Bikeman 2: om Ho Yeu That'),
(322, 'Kẻ Ăn Giấc Ngủ', 'Awoken', 'https://cdn.moveek.com/media/cache/short/5dfd7613ae765242745602.jpg', 'Trong quá trình giúp em trai mình thoát khỏi chứng bệnh mất ngủ do lỗi mã gen, cô sinh viên y khoa trẻ tuổi đã khám phá ra lí do thực sự ẩn sau căn bệnh của em mình.', '2020-01-17', 87, 0, '', 3, 'https://moveek.com/phim/awoken/', 10, 0, 'Ke an Giac Ngu'),
(323, 'The Queen of Black Magic', 'The Queen of Black Magic', 'https://cdn.moveek.com/media/cache/short/5dfd7673d01af505673672.jpg', 'Families were terrorized at the orphanage. Someone wants them dead, apparently with black magic that is very deadly. She has a grudge and she was also born because of the sins of the orphans who formed her into the queen of black magic.', '2020-01-31', 99, 0, '', 3, 'https://moveek.com/phim/the-queen-of-black-magic/', 10, 0, 'The Queen of Black Magic'),
(324, 'The Photograph', 'The Photograph', 'https://moveek.com/bundles/ornweb/img/no-poster.png', 'Intertwining love stories in the past and present.', '2020-02-13', 0, 0, '', 3, 'https://moveek.com/phim/the-photograph/', 10, 0, 'The Photograph'),
(325, 'Đảo Kinh Hoàng', 'Fantasy Island', 'https://cdn.moveek.com/media/cache/short/5dca278d7c1e9810614759.jpg', 'Câu chuyện về quý ngài Roarke và trợ lý Tatto, chào mừng các vị khách đến hòn đảo biệt lập của mình, và hứa hẹn 1 cuộc sống trong mơ.', '2020-02-14', 0, 0, 'https://www.youtube.com/embed/JhaOd066oWU?hd=1', 3, 'https://moveek.com/phim/fantasy-island/', 10, 0, 'dao Kinh Hoang');
